import React, { useState, useEffect, useRef } from "react";
import PropTypes from "prop-types";
import axios from "../axios";

// const baseURL = "https://api.jikan.moe/v4/";

function AnimeRow({ title, fetchUrl }) {
  const [animes, setAnimes] = useState([]);
  const animeRowRef = useRef(null);
  const imagesPerScroll = 6;
  const itemWidth = 300;

  useEffect(() => {
    async function fetchAnimeData() {
      const request = await axios.get(fetchUrl);
      console.log(request.data);
      setAnimes(request.data.data);
      console.log(animes);
      return request;
    }
    fetchAnimeData();
  }, [fetchUrl]);

  const handleHorizantalScroll = (element, speed, distance, step) => {
    let scrollAmount = 0;
    const slideTimer = setInterval(() => {
      element.scrollLeft += step;
      scrollAmount += Math.abs(step);
      if (scrollAmount >= distance) {
        clearInterval(slideTimer);
      }
      if (element.scrollLeft === 0) {
        setArrowDisable(true);
      } else {
        setArrowDisable(false);
      }
    }, speed);
  };

  
  const scrollLeft = () => {
    console.log("left");
    console.log(animeRowRef);
    const currentScroll = animeRowRef.current.scrollLeft;
    animeRowRef.current.scrollTo({
      left: currentScroll - imagesPerScroll * itemWidth,
      behavior: "smooth",
    });
  };

  const scrollRight = () => {
    console.log("right");
    console.log(animeRowRef);
    const currentScroll = animeRowRef.current.scrollLeft;
    animeRowRef.current.scrollTo({
      left: currentScroll + imagesPerScroll * itemWidth,
      behavior: "smooth",
    });
  };

  return (
    <>
      <div className="container--animeRow--anime" ref={animeRowRef}>
        <i className="fa-solid fa-angle-left" onClick={scrollLeft}></i>
        <h1>Anime {title}</h1>
        <div className="container--animeRow--anime--list">
          {animes.length > 0 ? (
            animes.map((anime) => (
              <div className="itemes" key={anime.mal_id}>
                <a href="#" target="_blank" rel="noopener noreferrer">
                  <img
                    src={anime.images.jpg.large_image_url}
                    alt={anime.title}
                  />
                </a>
              </div>
            ))
          ) : (
            <p>Loading Anime List...</p>
          )}
        </div>

        <i className="fa-solid fa-angle-right" onClick={() => scrollRight}></i>
      </div>
    </>
  );
}
AnimeRow.propTypes = {
  title: PropTypes.string.isRequired,
  fetchUrl: PropTypes.string.isRequired,
};
export default AnimeRow;
