import React, { useState, useEffect, useRef } from "react";
import PropTypes from "prop-types";
import axios from "axios";

function AnimeRow({ title, data }) {
  const animeRowRef = useRef(null);
  const imagesPerScroll = 6;
  const itemWidth = 300;
  const maxItems = 10;

  /*useEffect(() => {
    const options = {
      method: "GET",
      url: "https://anime-db.p.rapidapi.com/anime",
      params: {
        page: "1",
        size: "10",
        sortBy: "ranking",
        sortOrder: "asc",
      },
      headers: {
        "X-RapidAPI-Key": "f009e8294dmshaea6eeead8d70fcp14eed1jsn633681721e75",
        "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
      },
    };

    async function getAnimes() {
      const animes = await axios.request(options);
      console.log(animes.data);
      setImages(animes.data.data);
    }
    getAnimes();
  }, []);
*/
  const scrollLeft = () => {
    const currentScroll = animeRowRef.current.scrollLeft;
    animeRowRef.current.scrollTo({
      left: currentScroll - imagesPerScroll * itemWidth,
      behavior: "smooth",
    });
  };

  const scrollRight = () => {
    const currentScroll = animeRowRef.current.scrollLeft;
    animeRowRef.current.scrollTo({
      left: currentScroll + imagesPerScroll * itemWidth,
      behavior: "smooth",
    });
  };

  return (
    <>
      <div className="container--animeRow--anime">
        <i className="fa-solid fa-angle-left" onClick={scrollLeft}></i>
        <h1>Anime {title}</h1>

        <div className="container--animeRow--anime--list">
          {data.length > 0 ? (
            data.slice(0, maxItems).map((anime, index) => (
              <div key={index} className="itemes">
                <a href="#" target="_blank" rel="noopener noreferrer">
                  <img src={anime.image} alt={`Anime ${index}`} />
                </a>
              </div>
            ))
          ) : (
            <p>Loading...</p>
          )}
        </div>

        <i className="fa-solid fa-angle-right" onClick={scrollRight}></i>
      </div>
    </>
  );
}

export default AnimeRow;

AnimeRow.propTypes = {
  title: PropTypes.string.isRequired,
  data: PropTypes.array.isRequired,
};
