import React, { useState, useEffect, useRef } from "react";
import PropTypes from "prop-types";

function AnimeTable({ title, data }) {
  const [images, setImages] = useState([]);

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://anime-db.p.rapidapi.com/anime",
      params: {
        page: "1",
        size: "10",
        sortBy: "ranking",
        sortOrder: "asc",
      },
      headers: {
        "X-RapidAPI-Key": "f009e8294dmshaea6eeead8d70fcp14eed1jsn633681721e75",
        "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
      },
    };

    async function getAnimes() {
      const animes = await axios.request(options);
      console.log(animes.data);
      setImages(animes.data.data);
    }
    getAnimes();
  }, []);

  return (
    <>
      <h1>Anime {title}</h1>
      <div className="container--animeRow--anime--all--list">
        {images.map((anime, index) => (
          <div key={index} className="itemes">
            <a href="#" target="_blank" rel="noopener noreferrer">
              <img src={anime.image} alt={`${index}`} />
            </a>
          </div>
        ))}
      </div>
    </>
  );
}

export default AnimeTable;

AnimeTable.propTypes = {
  title: PropTypes.string.isRequired,
  data: PropTypes.array.isRequired
};
