import React, { useState, useEffect } from "react";
import requests from "../request";
import AnimeTable from "./AnimeTable";
import AnimeRow from "./AnimeRow"
import { useGlobalContext } from "../context/global"

function Home() {
  const {
    popularAnime,
    upcomingAnime,
    airingAnime,
    loading,
  } = useGlobalContext();

  useEffect(() => {
    getPopularAnime(); // Call the getPopularAnime function from the context
    getUpcomingAnime();
  }, []);

  return (
    <>
      <div className="container--animeRow">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <AnimeRow title="Top 10 Hit" animes={popularAnime} />
          <AnimeRow title="Now Airing" animes={airingAnime} />
          <AnimeRow title="Upcoming" animes={upcomingAnime} />
        </>
      )}
      {/* <AnimeRow title='Top Hit' fetchUrl={requests.fetchPopular} />
      <AnimeRow title='Now Airing' fetchUrl={requests.fetchTopAiring} />
      <AnimeTable  title='Fatasy' fetchUrl={requests.fetchAllTopAiring} />  */}
      </div>
    </>
  );
}
export default Home;
