import React, { useState, useEffect } from "react";
import AnimeRow from "./AnimeRow";
import AnimeTable from "./AnimeTable";
import axios from "axios";

function Home() {
  const [animeData, setAnimeData] = useState([]);
  const [animeCategories] = useState([
    { title: "Award Winning", params: { genres: "Award Winning" } },
    //{ title: "Fantasy Hit", params: { genres: "Fantasy" } },
    //{ title: "Action Hit", params: { genres: "Action" } },
