import React, { useState, useEffect } from "react";
import AnimeRow from "./AnimeRow";
import AnimeTable from "./AnimeTable";
import axios from "axios";

function Home() {
  const [animeData, setAnimeData] = useState([]);
  const animeCategories = [
    { title: "Treading", params: { search: "Top", sortBy: "ranking", sortOrder: "asc" } },
   
  ];
  /*
  { title: "Treading", params: { search: "Top", sortBy: "ranking", sortOrder: "asc" } },
  { title: "Summer Hit", params: { search: "Summer", sortBy: "ranking", sortOrder: "asc" } },
    { title: "Winter Hit", params: { search: "Winter", sortBy: "ranking", sortOrder: "asc" } },
    { title: "Autumn Hit", params: { search: "Autumn", sortBy: "ranking", sortOrder: "asc" } },
    { title: "Fall Hit", params: { search: "Fall", sortBy: "ranking", sortOrder: "asc" } },
    { title: "Top picks", params: { search: "Fight", sortBy: "ranking", sortOrder: "asc" } },*/

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://anime-db.p.rapidapi.com/anime",
      params: {
        page: "1",
        size: "10",
        search: category.title,
        ...animeCategories.params,
      },
      headers: {
        "X-RapidAPI-Key": "f009e8294dmshaea6eeead8d70fcp14eed1jsn633681721e75",
        "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
      },
    };

    async function getAnimes() {
      const animes = await axios.request(options);
      console.log(animes.data);
      setAnimeData(animes.data.data);
    }

    animeCategories.forEach((category) => {
      getAnimes(category.params);
    });
  }, []);

  return (
    <>
      {animeCategories.map((category) => (
        <AnimeRow
          key={category.title}
          title={category.title}
          data={animeData}
        />
      ))}
    </>
  );
}
export default Home;
