import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import axios from "../axios";
import { Link } from "react-router-dom";

function AnimeTable({ title, fetchUrl }) {
  const [animes, setAnimes] = useState([]);

  useEffect(() => {
    async function fetchAnimeData() {
      const request = await axios.get(fetchUrl);
      console.log(request.data);
      setAnimes(request.data.data);
      console.log(animes);
      return request;
    }
    fetchAnimeData();
  }, [fetchUrl]);

  return (
    <>
      <h1>Anime {title}</h1>
      <div className="container--animeRow--anime--all--list">
        {animes.length > 0 ? (
          animes.map((anime) => (
            <div className="itemes" key={anime.mal_id}>
              <Link to={`/anime-details/${anime.id}`}>
                <img src={anime.images.jpg.large_image_url} alt={anime.title} />
              </Link>
            </div>
          ))
        ) : (
          <p>Loading Anime List...</p>
        )}
      </div>
    </>
  );
}

AnimeTable.propTypes = {
  title: PropTypes.string.isRequired,
  fetchUrl: PropTypes.string.isRequired,
};
export default AnimeTable;
