import React, { useState, useEffect } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import axios from "axios";

function AnimeRow() {
  const [images, setImages] = useState([]);
  const [scrollX, setScrollX] = useState(0);

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://anime-db.p.rapidapi.com/anime",
      params: {
        page: "1",
        size: "10",
        sortBy: "ranking",
        sortOrder: "asc",
      },
      headers: {
        "X-RapidAPI-Key": "8c1dd609c0msh55007741e312580p1be040jsn871593d54e0c",
        "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
      },
    };

    async function getAnimes() {
      const animes = await axios.request(options);
      console.log(animes.data);
      setImages(animes.data.data);
    }
    getAnimes();
  }, []);

  const prevSlide = () => {
    console.log("leftclick");
    /*setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );*/
    let x = scrollX + Math.round(window.innerWidth / 2);
    if (x > 0) {
      x = 0;
    }
    setScrollX(x);
  };
  const nextSlide = () => {
    console.log("rightclick");
    /*setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );*/
    let x = scrollX - Math.round(window.innerWidth / 2);
    let listW = anime.results.length * 150;
    if (window.innerWidth - listW > x) {
      x = window.innerWidth - listW - 60;
    }
    setScrollX(x);
  };
  /*
  const prevSlide = () => {
    setScrollPosition((prevPosition) => prevPosition + 100);
  };
  const nextSlide = () => {
    setScrollPosition((prevPosition) => prevPosition - 100);
  };
*/

  return (
    <>
      <div className="container--animeRow--anime">
        <i className="fa-solid fa-angle-left" onClick={prevSlide}></i>
        <h1>Anime Hot Hit</h1>

        <div className="container--animeRow--anime--list">
          {images.map((anime, index) => (
            <div key={index} className="itemes">
              <a href={anime.link} target="_blank" rel="noopener noreferrer">
                <img src={anime.image} alt={`Image ${index}`} />
              </a>
            </div>
          ))}
        </div>

        <i className="fa-solid fa-angle-right" onClick={nextSlide}></i>
      </div>
    </>
  );
}

export default AnimeRow;
