import React, { useState, useEffect } from "react";
import AnimeRow from "./componants/AnimeRow";
import AnimeTable from "./componants/AnimeTable";
import axios from 'axios';

let currentProductId = 9;

function Home() {
  const [products, setProducts] = useState([]);

  function addProduct(product) {
    const newProduct = { id: ++currentProductId, ...product };
    setProducts([...products, newProduct]);
  }
  
  useEffect(() => {
    async function getProducts() {
      const products = await axios.get(
        'https://run.mocky.io/v3/a091c6f3-dd51-43c8-973a-c7b6fd07a84a'
      );
      setProducts(products.data);
    }
  
    getProducts();
  }, []); // Put the empty array to make sure that the hook is executed only once

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://anime-db.p.rapidapi.com/anime",
      params: {
        page: "1",
        size: "10",
        sortBy: "ranking",
        sortOrder: "asc",
      },
      headers: {
        "X-RapidAPI-Key": "8c1dd609c0msh55007741e312580p1be040jsn871593d54e0c",
        "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
      },
    };

    async function getAnimes() {
      const animes = await axios.request(options);
      console.log(animes.data);
      setImages(animes.data.data);
    }
    getAnimes();
  }, []);

  return (
    <>
      {products.length > 0 ? (
        <ul className="Home__products">
          {products.map((product) => (
            <Product key={product.id} item={product} />
          ))}
        </ul>
      ) : (
        <div>Loading products....</div>
      )}
      <AddForm addProduct={addProduct} />
    </>
  );
}
export default Home;
