import React, { useEffect, useState } from "react";
import AnimeTable from "./AnimeTable";
import AnimeRow from "./AnimeRow";
import { useGlobalContext } from "../context/global";


function Genres() {

  const { popularAnime, loading } = useGlobalContext();

  const [actionAnime, setActionAnime] = useState([]);

  useEffect(() => {
    // Filter the anime with the genre name "Action"
    const actionAnimes = popularAnime.filter(anime => {
      return anime.genres.some(genreObj => genreObj.name === "Fantasy");
    });
    setActionAnime(actionAnimes);
  }, [popularAnime]);

  return (
    <div className="container--animeRow">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <AnimeRow title="Top 10 Action Hit" animes={actionAnime} />
          <AnimeTable title="Action" animes={actionAnime} />
        </>
      )}
    </div>
  );
}

export default Genres;
