import React, { useState, useEffect } from "react";
import AnimeRow from "./AnimeRow";
import AnimeTable from "./AnimeTable";
import axios from "axios";

function Home() {
  const [animeData, setAnimeData] = useState([]);
  const animeCategories = [
    { title: "Hot Hit", params: { sortBy: "ranking" } },
    { title: "Winter Hit", params: { search: "Winter"} },

  ];
   /*
  { title: "Summer Hit", params: { search: "Summer"} },
    { title: "Winter Hit", params: { search: "Winter"} },
    { title: "Autumn Hit", params: { search: "Autumn"} },
    { title: "Fall Hit", params: { search: "Fall"} },
    { title: "Top picks", params: { search: "Fight"} },*/

  useEffect(() => {
    async function getAnimes(title, params) {
      const options = {
        method: "GET",
        url: "https://anime-db.p.rapidapi.com/anime",
        params: {
          page: "1",
          size: "10",
          ...params,
        },
        headers: {
          "X-RapidAPI-Key":
            "37cb7c6b14msh0e4bfef14681545p11e89cjsn78cd09bd4f7e",
          "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
        },
      };

      try {
        const animes = await axios.request(options);
        console.log(animes.data);
        setAnimeData(animes.data.data);
      } catch (error) {
        console.error("Error fetching anime data:", error);
        return [];
      }
    }

    animeCategories.forEach((category) => {
      getAnimes(category.title, category.params);
    });
  }, []);

  /*useEffect(() => {
    const options = {
      method: "GET",
      url: "https://anime-db.p.rapidapi.com/anime",
      params: {
        page: "1",
        size: "10",
        sortBy: "ranking",
        sortOrder: "asc" ,
        ...params,
      },
      headers: {
        "X-RapidAPI-Key": "37cb7c6b14msh0e4bfef14681545p11e89cjsn78cd09bd4f7e",
        "X-RapidAPI-Host": "anime-db.p.rapidapi.com",
      },
    };

    async function getAnimes() {
      const animes = await axios.request(options);
      console.log(animes.data);
      setAnimeData(animes.data.data);
    }
    animeCategories.forEach((category) => {
      getAnimes(category.title, category.params);
    });
  }, []);*/

  return (
    <>
      {animeCategories.map((category) => (
        <AnimeRow
          key={category.title}
          title={category.title}
          data={animeData}
        />
      ))}
    </>
  );
}
export default Home;
