import React, { useState, useEffect  } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import axios from "axios";

function AnimeRow({}) {
    const [images, setImages] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
  
    useEffect(() => {
        const options = {
            method: 'GET',
            url: 'https://anime-db.p.rapidapi.com/anime',
            params: {
              page: '1',
              size: '10',
              search: 'Fullmetal',
              genres: 'Fantasy,Drama',
              sortBy: 'ranking',
              sortOrder: 'asc'
            },
            headers: {
              'X-RapidAPI-Key': '8c1dd609c0msh55007741e312580p1be040jsn871593d54e0c',
              'X-RapidAPI-Host': 'anime-db.p.rapidapi.com'
            }
          };
    
        axios.get(options.url, { headers: options.headers })
          .then(response => {
            setImages(response.data);
          })
          .catch(error => {
            console.error('Error fetching anime:', error);
          });
      }, []);
    

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <>
      <div class="container--animeRow--anime">
        <i class="fa-solid fa-angle-left" onClick={prevSlide}></i>
        <h1>Anime Hot Hit</h1>

        <div class="container--animeRow--anime--list">
          <Carousel
            selectedItem={currentIndex}
            showThumbs={false}
            showStatus={false}
            showIndicators={false}
            infiniteLoop={true}
          >
            {images.map((imageUrl, index) => (
              <div key={index}>
                <img href='#' target='_blank'src={imageUrl} alt={`Image ${index}`} />
              </div>
            ))}
          </Carousel>
        </div>

        <i class="fa-solid fa-angle-right" onClick={nextSlide}></i>
      </div>
    </>
  );
}

export default AnimeRow;
